import React from "react";
import PlacesAutocomplete, {
  geocodeByAddress,
  // geocodeByPlaceId,
  getLatLng
} from "react-places-autocomplete";

class Departure extends React.Component {
  constructor({ globalState, setGlobalState }) {
    super({ globalState, setGlobalState });
    this.state = { ...globalState };
    [setGlobalState] = this.setState.bind(this);
    this.handleDeparture = this.handleDeparture.bind(this);
    this.handleSelect = this.handleSelect.bind(this);
  }

  handleDeparture = address => {
    this.setState({ departure: { address: address } });
  };

  handleSelect = address => {
    geocodeByAddress(address)
      .then(results => getLatLng(results[0]))
      .then(latLng => {
        this.setState({
          departure: { address: address, lat: latLng.lat, lng: latLng.lng }
        });
        console.log(
          "Success departure address",
          `latitude : ${this.state.departure.lat}`,
          `longitude : ${this.state.departure.lng}`,
          `address : ${this.state.departure.address}`
        );
        console.log("globalState : ", this.state);
      })
      // .then(address => this.setState({ departure: address }))
      .catch(error => console.error("Error", error));
  };

  render() {
    return (
      <>
        <PlacesAutocomplete
          value={this.state.departure.address}
          onChange={this.handleDeparture}
          onSelect={this.handleSelect}
          shouldFetchSuggestions={this.state.departure.address.length > 3}
        >
          {({
            getInputProps,
            suggestions,
            getSuggestionItemProps,
            loading
          }) => (
            <div>
              <input
                id="departure"
                placeholder="Adresse de dÃ©part"
                style={{
                  lineHeight: "1.2em",
                  width: "250px",
                  borderTop: "none",
                  borderLeft: "none",
                  borderRight: "none",
                  borderBottom: "1px solid #6767AD",
                  fontSize: "1.3em",
                  outline: "none",
                  padding: "20px",
                  margin: "10px",
                  background: "rgba(255, 255, 255, 0)",
                  color: "#241F5D"
                }}
                {...getInputProps({
                  // placeholder: "Search Places ...",
                  className: "location-search-input"
                })}
              />
              <div className="autocomplete-dropdown-container">
                {loading && <div>Loading...</div>}
                {suggestions.map(suggestion => {
                  const placeName = suggestion.active
                    ? suggestion.description
                    : "";
                  const className = suggestion.active
                    ? "suggestion-item--active"
                    : "suggestion-item";
                  // inline style for demonstration purpose
                  const style = suggestion.active
                    ? {
                        lineHeight: "1.2em",
                        width: "250px",
                        color: "#241F5D",
                        backgroundColor: "rgba(255, 255, 255, 50)",
                        cursor: "pointer"
                      }
                    : {
                        color: "#241F5D",
                        backgroundColor: "rgba(255, 255, 255, 50)",
                        cursor: "pointer"
                      };

                  return (
                    <div
                      {...getSuggestionItemProps(suggestion, {
                        className,
                        style
                      })}
                    >
                      <span>{suggestion.description}</span>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </PlacesAutocomplete>
      </>
    );
  }
}

export default Departure;
