import {
  GoogleMapLibrary,
  GroundOverlayOptions,
  HeatmapLayerOptions,
} from './types'

export const NYC_LATLNG: google.maps.LatLngLiteral = {
  lat: 40.7128,
  lng: -74.006,
}
export const NYC_POLYGON: google.maps.LatLngLiteral[] = [
  {lat: 40.718, lng: -74.006},
  {lat: 40.718, lng: -74.01},
  {lat: 40.7138, lng: -74.001},
  {lat: 40.7128, lng: -74.005},
]
export const NYC_RECTANGLE: google.maps.LatLngBoundsLiteral = {
  east: -74,
  west: -74.006,
  north: 40.718,
  south: 40.714,
}

const DEFAULT_MAP_ZOOM = 14

export const DEFAULT_CIRCLE_OPTIONS: google.maps.CircleOptions = {
  center: NYC_LATLNG,
  radius: 200,
}

export const DEFAULT_DRAWING_MANAGER_OPTIONS: google.maps.drawing.DrawingManagerOptions = {}

export const DEFAULT_GROUND_OVERLAY_OPTIONS: GroundOverlayOptions = {
  url: 'https://placehold.it/256x256',
  bounds: NYC_RECTANGLE,
}

export const DEFAULT_HEAT_MAP_OPTIONS: HeatmapLayerOptions = {
  data: [{lat: 40.718, lng: -74.006}, {lat: 40.712, lng: -74.01}],
  radius: 100,
}

export const DEFAULT_INFO_WINDOW_OPTIONS: google.maps.InfoWindowOptions = {
  content: 'Hello',
  position: NYC_LATLNG,
}

export const DEFAULT_KML_LAYER_OPTIONS: google.maps.KmlLayerOptions = {
  url:
    'http://api.flickr.com/services/feeds/geo/?g=322338@N20&lang=en-us&format=feed-georss',
}

export const DEFAULT_MAP_OPTIONS: google.maps.MapOptions = {
  center: NYC_LATLNG,
  zoom: DEFAULT_MAP_ZOOM,
}

export const DEFAULT_MAP_STYLE = {
  height: '100%',
  width: '100%',
}

export const DEFAULT_MARKER_OPTIONS: google.maps.MarkerOptions = {
  position: NYC_LATLNG,
}

export const DEFAULT_POLYGON_OPTIONS: google.maps.PolygonOptions = {
  paths: NYC_POLYGON,
}

export const DEFAULT_POLYLINE_OPTIONS: google.maps.PolylineOptions = {
  path: NYC_POLYGON,
}

export const DEFAULT_RECTANGLE_OPTIONS: google.maps.RectangleOptions = {
  bounds: NYC_RECTANGLE,
}

export const DEFAULT_SEARCH_BOX_OPTIONS: google.maps.places.SearchBoxOptions = {
  bounds: NYC_RECTANGLE,
}

export const DEFAULT_AUTOCOMPLETE_OPTIONS: google.maps.places.AutocompleteOptions = {
  bounds: NYC_RECTANGLE,
}

export const DEFAULT_STREET_VIEW_OPTIONS: google.maps.StreetViewPanoramaOptions = {
  position: NYC_LATLNG,
}

export const GOOGLE_MAP_BASE_URI = 'https://maps.googleapis.com/maps/api/js'

export const GOOGLE_MAP_LIBRARY_NAMES: GoogleMapLibrary[] = [
  'drawing',
  'geometry',
  'places',
  'visualization',
]
import {useState, useEffect} from 'react'
import loadjs from 'loadjs'
import {GOOGLE_MAP_BASE_URI} from '../common/constants'

interface GoogleAPIProps {
  apiKey: string
  libraryParam: string
  languageParam: string
  regionParam: string
}

const useGoogleAPI = ({
  apiKey,
  libraryParam,
  languageParam,
  regionParam,
}: GoogleAPIProps) => {
  const [loaded, setLoaded] = useState(false)
  useEffect(() => {
    // Should not load script at server side
    if (typeof document === 'undefined') return

    const googleMapScriptUri = `${GOOGLE_MAP_BASE_URI}?key=${apiKey}${libraryParam}${languageParam}${regionParam}`
    if (!loadjs.isDefined('gmap')) loadjs(googleMapScriptUri, 'gmap')
    loadjs.ready('gmap', {
      success: () => {
        setLoaded(true)
      },
      error: () => {
        loadjs.reset()
        console.error('Unable to fetch Google Map sdk')
      },
    })
  }, [])
  return loaded
}

export default useGoogleAPI
import {useEffect} from 'react'

interface GoogleMapEvent {
  name: string
  handler?: (...args: any[]) => void
}

const useGoogleListener = (
  instance: google.maps.MVCObject | undefined,
  events: GoogleMapEvent[],
) => {
  useEffect(() => {
    if (instance === undefined) return
    const listeners: google.maps.MapsEventListener[] = []
    events.forEach(event => {
      if (event.handler)
        listeners.push(
          google.maps.event.addListener(instance, event.name, event.handler),
        )
    })
    return () => {
      listeners.forEach(listener => listener.remove())
    }
  }, [instance, events])
}

export default useGoogleListener
import {useEffect} from 'react'
import {GoogleMapObjectWithSetOptions} from '../common/types'

const useMemoizedOptions = (
  instance: GoogleMapObjectWithSetOptions | undefined,
  opts: any,
  prevOpts: string,
  setPrevOpts: React.Dispatch<React.SetStateAction<string>>,
) => {
  useEffect(() => {
    if (
      instance === undefined ||
      opts === undefined ||
      JSON.stringify(opts) === prevOpts
    )
      return
    instance.setOptions(opts)
    setPrevOpts(JSON.stringify(opts))
  }, [instance, opts])
}

export default useMemoizedOptions
import React, {useReducer} from 'react'
import {
  GoogleMapAction,
  GoogleMapObject,
  GoogleMapObjectWithSetMap,
  GoogleMapProviderProps,
  GoogleMapReducer,
  GoogleMapState,
} from '../common/types'

const initialState = (): GoogleMapState => ({
  map: undefined,
  objects: new Map<string, GoogleMapObject>(),
  places: undefined,
})

const GoogleMapContext = React.createContext<GoogleMapReducer>({
  state: (undefined as unknown) as GoogleMapState,
  dispatch: (undefined as unknown) as React.Dispatch<GoogleMapAction>,
})

const reducer = (state: GoogleMapState, action: GoogleMapAction) => {
  switch (action.type) {
    case 'reset':
      return initialState()

    case 'init_map':
      if (action.map === undefined)
        throw new Error('You should specify a map instance')
      if (state.map !== undefined)
        throw new Error('There can only be one map instance in a context')

      return {...state, map: action.map, places: action.places}

    case 'add_object':
      if (action.object === undefined)
        throw new Error('You should specify an object instance')
      if (action.id === undefined) throw new Error('You should specify an id')
      if (state.objects.has(action.id))
        throw new Error('The id has already been taken')
      state.objects.set(action.id, action.object)

      return state

    case 'remove_object':
      if (action.id === undefined) throw new Error('You should specify an id')
      const objectToRemove = state.objects.get(action.id)
      if (objectToRemove === undefined)
        throw new Error('There is no object with the given id')

      // If the object can setMap, then setMap to null
      if ((objectToRemove as GoogleMapObjectWithSetMap).setMap)
        (objectToRemove as GoogleMapObjectWithSetMap).setMap(null)
      state.objects.delete(action.id)

      return state

    default:
      return state
  }
}

const GoogleMapProvider = ({children}: GoogleMapProviderProps) => {
  const [state, dispatch] = useReducer(reducer, initialState())
  const value = {state, dispatch}

  return (
    <>
      <GoogleMapContext.Provider value={value}>
        {children}
      </GoogleMapContext.Provider>
    </>
  )
}

const GoogleMapConsumer = GoogleMapContext.Consumer

export {GoogleMapContext, GoogleMapProvider, GoogleMapConsumer}
import React, {useContext, useEffect, useState} from 'react'
import ReactDOMServer from 'react-dom/server'
import uuid from 'uuid/v1'
import {useGoogleListener, useMemoizedOptions} from '../hooks'
import {DEFAULT_AUTOCOMPLETE_OPTIONS} from '../common/constants'
import {BasicAutocompleteProps} from '../common/types'
import {GoogleMapContext} from '../contexts/GoogleMapContext'

const BasicAutocomplete = ({
  id,
  opts = DEFAULT_AUTOCOMPLETE_OPTIONS,
  onPlaceChanged,
  bindingPosition,
  ...restProps
}: BasicAutocompleteProps) => {
  const {state, dispatch} = useContext(GoogleMapContext)
  const [prevOpts, setPrevOpts] = useState('')
  const [autocomplete, setAutocomplete] = useState<
    google.maps.places.Autocomplete | undefined
  >(undefined)
  const [autocompleteId] = useState(id ? id : `autocomplete-${uuid()}`)
  const [container] = useState(
    document
      .createRange()
      .createContextualFragment(
        ReactDOMServer.renderToString(
          <input id={autocompleteId} {...restProps} />,
        ),
      ).firstElementChild,
  )
  const [lastBindingPosition, setLastBindingPosition] = useState(
    bindingPosition,
  )

  const addAutocomplete = (autocomplete: google.maps.places.Autocomplete) =>
    dispatch({type: 'add_object', object: autocomplete, id: autocompleteId})
  const removeAutocomplete = () =>
    dispatch({type: 'remove_object', id: autocompleteId})

  // Create google.maps.places.Autocomplete
  useEffect(() => {
    if (state.map === undefined || state.places === undefined) return
    const inputNode = (bindingPosition
      ? container
      : document.getElementById(autocompleteId)) as HTMLInputElement
    const autocomplete = new google.maps.places.Autocomplete(inputNode, opts)
    setAutocomplete(autocomplete)
    addAutocomplete(autocomplete)
    setPrevOpts(JSON.stringify(opts))
    if (bindingPosition) {
      if (bindingPosition !== lastBindingPosition) {
        const last =
          state.map.controls[google.maps.ControlPosition[lastBindingPosition!]]
        const lastArray = last.getArray()
        last.removeAt(lastArray.findIndex(element => element === container))
        setLastBindingPosition(bindingPosition)
      }
      state.map.controls[google.maps.ControlPosition[bindingPosition]].push(
        inputNode,
      )
    }
    return () => removeAutocomplete()
  }, [state.places, bindingPosition])

  // Register google map event listeners
  useGoogleListener(autocomplete, [
    {name: 'place_changed', handler: onPlaceChanged},
  ])

  // Modify the google.maps.Autocomplete object when component props change
  useMemoizedOptions(autocomplete, opts, prevOpts, setPrevOpts)

  return bindingPosition ? null : <input id={autocompleteId} {...restProps} />
}

