import React from "react";
import {
  Accordion,
  AccordionItem,
  AccordionItemHeading,
  AccordionItemButton,
  AccordionItemPanel
} from "react-accessible-accordion";
import "./Accordeon.css";
import Quotes from "../../images/icones/Quotes";
import Transportation from "../../images/Transportation";
import styled from "styled-components";
import MobiliteFirstLogo from "../../images/MobiliteFirstLogo";
import FormArroundMe from "../FormArroundMe/FormArroundMe";
import ItineraryMap from "../ItineraryMap/ItineraryMap";
import FormTravel from "../FormTravel/FormTravel";

const Container = styled.div`
 width: 100%;
 margin-right:0px;
 padding-right:0px;
 padding-left:0px;
 margin:0px;
}
 p{
     padding:0px;
     margin-top:0px;
 }
 h2{
     padding:0px;
     margin:0px;
 }
 `;

export default function Accordeon(props) {
  const { globalState, setGlobalState } = props;
  return (
    <>
      <Accordion allowZeroExpanded={true} preExpanded={"mobility"}>
        <AccordionItem uuid="mobility">
          <AccordionItemHeading>
            <AccordionItemButton className="">
              <Container>
                <MobiliteFirstLogo />
              </Container>
            </AccordionItemButton>
          </AccordionItemHeading>
          <AccordionItemPanel>
            <Container>
              <Quotes />
              <h2>COMMANDEZ</h2>
              <p>votre trajet multi-modal</p>
              <Transportation />
            </Container>
          </AccordionItemPanel>
        </AccordionItem>
        <AccordionItem>
          <AccordionItemHeading>
            <AccordionItemButton className="bg-color-plum">
              <h2>Rechercher un itin√©raire </h2>
            </AccordionItemButton>
          </AccordionItemHeading>
          <AccordionItemPanel>
            <FormTravel
              globalState={globalState}
              setGlobalState={setGlobalState}
            />
            <ItineraryMap
              globalState={globalState}
              setGlobalState={setGlobalState}
            />
          </AccordionItemPanel>
        </AccordionItem>
        <AccordionItem>
          <AccordionItemHeading>
            <AccordionItemButton className="bg-color-orange">
              <h2>Autour de moi</h2>
            </AccordionItemButton>
          </AccordionItemHeading>
          <AccordionItemPanel>
            <FormArroundMe
              globalState={globalState}
              setGlobalState={setGlobalState}
            />
          </AccordionItemPanel>
        </AccordionItem>
      </Accordion>
    </>
  );
}
